// ==UserScript==
// @name         Disable Grok Imagine Video Autoplay with Hover Control
// @namespace    http://tampermonkey.net/
// @version      2.0
// @description  Disable autoplay, add visual indicators, and enable hover-to-play on Grok Imagine
// @author       Your Name
// @match        https://grok.com/imagine/*
// @match        https://grok.com/imagine*
// @grant        none
// @run-at       document-start
// ==/UserScript==

(function() {
    'use strict';

    // Configuration
    const CONFIG = {
        preloadSetting: 'metadata', // Options: 'none', 'metadata', 'auto'
        logActions: false, // Set to true to see console logs for debugging
        outlineColors: {
            hadAutoplay: '#f9f8f6',    // Outline for cards that had autoplay
            noAutoplay: '#fcfcfc1a'     // Outline for cards without autoplay
        },
        outlineWidth: '2px',
        outlineStyle: 'solid'
    };

    /**
     * Find the card container for a video element
     * @param {HTMLVideoElement} video - The video element
     * @returns {HTMLElement|null} - The card container element
     */
    function findCardContainer(video) {
        // Look for the parent with role="listitem" or the masonry card div
        let element = video;
        let maxDepth = 10; // Prevent infinite loops

        while (element && maxDepth > 0) {
            element = element.parentElement;
            if (!element) break;

            // Check if this is a listitem or masonry card container
            if (element.getAttribute('role') === 'listitem' ||
                element.classList.contains('group/media-post-masonry-card')) {
                return element;
            }

            maxDepth--;
        }

        return null;
    }

    /**
     * Apply outline to a card container
     * @param {HTMLElement} container - The card container element
     * @param {string} color - The outline color
     */
    function applyOutline(container, color) {
        if (!container) return;

        container.style.outline = `${CONFIG.outlineWidth} ${CONFIG.outlineStyle} ${color}`;
        container.style.outlineOffset = '2px';

        if (CONFIG.logActions) {
            console.log('Applied outline color:', color, 'to container');
        }
    }

    /**
     * Setup hover-to-play functionality
     * @param {HTMLVideoElement} video - The video element
     * @param {HTMLElement} container - The card container element
     */
    function setupHoverToPlay(video, container) {
        if (!container) return;

        // Store original loop state
        const hadLoop = video.dataset.originalLoop === 'true';

        // Mouseenter handler - play video
        const handleMouseEnter = () => {
            if (hadLoop) {
                video.loop = true; // Restore loop for hover playback
            }
            video.play().catch(err => {
                if (CONFIG.logActions) {
                    console.log('Play failed on hover:', err);
                }
            });

            if (CONFIG.logActions) {
                console.log('Video playing on hover:', video.src);
            }
        };

        // Mouseleave handler - pause video and reset
        const handleMouseLeave = () => {
            video.pause();
            video.loop = false; // Remove loop when not hovering
            video.currentTime = 0; // Reset to beginning

            if (CONFIG.logActions) {
                console.log('Video paused on mouse leave:', video.src);
            }
        };

        // Add event listeners
        container.addEventListener('mouseenter', handleMouseEnter);
        container.addEventListener('mouseleave', handleMouseLeave);

        // Store reference to prevent duplicate listeners
        video.dataset.hoverEnabled = 'true';

        if (CONFIG.logActions) {
            console.log('Hover-to-play enabled for video:', video.src);
        }
    }

    /**
     * Process a single video element to disable autoplay and loop
     * @param {HTMLVideoElement} video - The video element to process
     */
    function processVideo(video) {
        // Check if this video has already been processed
        if (video.dataset.autoplayDisabled === 'true') {
            return;
        }

        const hadAutoplay = video.hasAttribute('autoplay');
        const hadLoop = video.hasAttribute('loop');

        // Store original state
        video.dataset.originalAutoplay = hadAutoplay.toString();
        video.dataset.originalLoop = hadLoop.toString();

        let modified = false;

        // Remove autoplay attribute
        if (hadAutoplay) {
            video.removeAttribute('autoplay');
            modified = true;
            if (CONFIG.logActions) console.log('Removed autoplay from video:', video.src);
        }

        // Remove loop attribute
        if (hadLoop) {
            video.removeAttribute('loop');
            modified = true;
            if (CONFIG.logActions) console.log('Removed loop from video:', video.src);
        }

        // Pause the video if it's currently playing
        if (!video.paused) {
            video.pause();
            modified = true;
            if (CONFIG.logActions) console.log('Paused video:', video.src);
        }

        // Set preload attribute to reduce bandwidth usage
        if (video.getAttribute('preload') !== CONFIG.preloadSetting) {
            video.setAttribute('preload', CONFIG.preloadSetting);
            modified = true;
        }

        // Reset currentTime to beginning if video has started
        if (video.currentTime > 0) {
            video.currentTime = 0;
            modified = true;
        }

        // Find the card container
        const cardContainer = findCardContainer(video);

        // Apply appropriate outline and setup hover functionality
        if (hadAutoplay) {
            // Card had autoplay - use special outline and enable hover-to-play
            applyOutline(cardContainer, CONFIG.outlineColors.hadAutoplay);
            setupHoverToPlay(video, cardContainer);
        } else {
            // Card didn't have autoplay - use different outline
            applyOutline(cardContainer, CONFIG.outlineColors.noAutoplay);
        }

        // Mark this video as processed
        video.dataset.autoplayDisabled = 'true';

        if (modified && CONFIG.logActions) {
            console.log('Video processed successfully:', video.src || 'inline video');
        }
    }

    /**
     * Process all video elements currently in the DOM
     */
    function processAllVideos() {
        const videos = document.querySelectorAll('video');
        if (CONFIG.logActions && videos.length > 0) {
            console.log(`Found ${videos.length} video(s) to process`);
        }
        videos.forEach(processVideo);
    }

    /**
     * Initialize the MutationObserver to watch for dynamically added videos
     */
    function initObserver() {
        // Create a MutationObserver to detect new video elements
        const observer = new MutationObserver((mutations) => {
            mutations.forEach((mutation) => {
                // Check added nodes for video elements
                mutation.addedNodes.forEach((node) => {
                    // Process if the node itself is a video
                    if (node.nodeType === Node.ELEMENT_NODE) {
                        if (node.tagName === 'VIDEO') {
                            processVideo(node);
                        }
                        // Also check if the node contains any video elements
                        if (node.querySelectorAll) {
                            const videos = node.querySelectorAll('video');
                            videos.forEach(processVideo);
                        }
                    }
                });
            });
        });

        // Start observing the document with configured parameters
        observer.observe(document.documentElement, {
            childList: true,      // Watch for added/removed nodes
            subtree: true,        // Watch all descendants
            attributes: false     // Don't watch attribute changes
        });

        if (CONFIG.logActions) {
            console.log('MutationObserver initialized for dynamic video detection');
        }
    }

    /**
     * Initialize the script
     */
    function init() {
        if (CONFIG.logActions) {
            console.log('Grok Video Autoplay Disabler: Initializing...');
        }

        // Process videos that might already exist in the DOM
        processAllVideos();

        // Initialize the observer for dynamically loaded content
        initObserver();

        // Also process videos when DOM is fully loaded (as a backup)
        if (document.readyState === 'loading') {
            document.addEventListener('DOMContentLoaded', processAllVideos);
        }

        // Additional check when page is fully loaded (including all resources)
        if (document.readyState !== 'complete') {
            window.addEventListener('load', processAllVideos);
        }

        if (CONFIG.logActions) {
            console.log('Grok Video Autoplay Disabler: Initialized successfully');
        }
    }

    // Run initialization
    // If DOM is already interactive or complete, run immediately
    if (document.readyState !== 'loading') {
        init();
    } else {
        // Otherwise wait for DOM to be interactive
        document.addEventListener('DOMContentLoaded', init);
    }

})();

content.text:
Great question! Here are the CSS outline styles:

## Standard Outline Styles:

1. **`solid`** - A single solid line (most common)
2. **`dashed`** - Dashed line with gaps
3. **`dotted`** - Series of dots
4. **`double`** - Two parallel solid lines
5. **`groove`** - 3D grooved effect (looks carved in)
6. **`ridge`** - 3D ridged effect (looks raised out)
7. **`inset`** - 3D inset effect (looks pressed in)
8. **`outset`** - 3D outset effect (looks raised)
9. **`none`** - No outline
10. **`hidden`** - Same as none

## The "Blur" Effect You're Thinking Of:

**None of these blur the color!** What you want is **`box-shadow`** which creates a glowing/blurred effect. Here's how to modify the script:

```javascript
// Instead of outline, use box-shadow for a blur effect
outlineColors: {
    hadAutoplay: '#f9f8f6',
    noAutoplay: '#fcfcfc1a'
},
useBoxShadow: true,  // Enable blur effect
blurRadius: '8px'     // Amount of blur
```

And in the `applyOutline` function, you'd change it to:

```javascript
function applyOutline(container, color) {
    if (!container) return;

    // Using box-shadow for blur/glow effect
    container.style.boxShadow = `0 0 ${CONFIG.blurRadius} ${color}`;

    if (CONFIG.logActions) {
        console.log('Applied blur shadow color:', color, 'to container');
    }
}
```

Would you like me to update the script to use the blurred shadow effect instead of a solid outline?

