    preloadSetting: 'metadata', // Options: 'none', 'metadata', 'auto'
    logActions: false, // Set to true to see console logs for debugging
    glowColors: {
        hadAutoplay: '#f9f8f6',    // Glow for cards that had autoplay
        noAutoplay: '#fcfcfc1a'     // Glow for cards without autoplay
    },
    glowBlur: '12px',              // Blur radius for the glow effect
    glowSpread: '0px'              // Optional spread radius
};

