input.new_str:
/**
 * Apply glow effect to a card container
 * @param {HTMLElement} container - The card container element
 * @param {string} color - The glow color
 */
function applyGlow(container, color) {
    if (!container) return;

    // Using box-shadow for glow/blur effect
    container.style.boxShadow = `0 0 ${CONFIG.glowBlur} ${CONFIG.glowSpread} ${color}`;
    container.style.borderRadius = '1rem'; // Match Grok's rounded corners

    if (CONFIG.logActions) {
        console.log('Applied glow color:', color, 'to container');
    }
}

input.old_str:
/**
 * Apply outline to a card container
 * @param {HTMLElement} container - The card container element
 * @param {string} color - The outline color
 */
function applyOutline(container, color) {
    if (!container) return;

    container.style.outline = `${CONFIG.outlineWidth} ${CONFIG.outlineStyle} ${color}`;
    container.style.outlineOffset = '2px';

    if (CONFIG.logActions) {
        console.log('Applied outline color:', color, 'to container');
    }
}

input.new_str:
// Find the card container
const cardContainer = findCardContainer(video);

// Apply appropriate glow effect and setup hover functionality
if (hadAutoplay) {
    // Card had autoplay - use special glow and enable hover-to-play
    applyGlow(cardContainer, CONFIG.glowColors.hadAutoplay);
    setupHoverToPlay(video, cardContainer);
} else {
    // Card didn't have autoplay - use different glow
    applyGlow(cardContainer, CONFIG.glowColors.noAutoplay);
}

