input.new_str:
/**
 * Check if we're on a page where the script should run
 * @returns {boolean} - True if script should run
 */
function shouldRunOnThisPage() {
    const url = window.location.pathname;

    // Don't run on individual post pages
    if (url.includes('/post/')) {
        if (CONFIG.logActions) {
            console.log('Grok Video Autoplay Disabler: Skipping - on individual post page');
        }
        return false;
    }

    // Run on favorites, explore, and other feed pages
    return true;
}

/**
 * Initialize the script
 */
function init() {
    // Check if we should run on this page
    if (!shouldRunOnThisPage()) {
        return; // Exit early if we're on a post page
    }

    if (CONFIG.logActions) {
        console.log('Grok Video Autoplay Disabler: Initializing...');
    }

    // Process videos that might already exist in the DOM
    processAllVideos();

