// ==UserScript==
// @name         Grok Video DevTools v27.0 (React Explorer)
// @namespace    http://tampermonkey.net/
// @version      27.0
// @description  React Fiber Explorer + God Mode Navigation + Passive History
// @author       You
// @match        https://grok.com/*
// @grant        none
// @run-at       document-start
// ==/UserScript==

(function () {
    'use strict';

    function main() {
        console.log("Grok DevTools v27.0: Initializing (React Explorer)...");

        // --- CONFIG ---
        const TARGET_ENDPOINT = '/rest/app-chat/conversations/new';
        const LIST_ENDPOINT = '/rest/media/post/list';

        // --- STATE ---
        const requests = new Map();
        const groups = new Map();
        let historyData = [];
        let historyOffset = 0;
        let hasLoadedHistory = false;

        // --- REACT EXPLORATION (Find internals for re-render) ---
        const ReactExplorer = {
            keys: null,
            findReactKeys: function () {
                if (this.keys) return this.keys;
                this.keys = { fiber: null, props: null, container: null, suffix: null };
                const testEl = document.querySelector('button') || document.querySelector('[data-testid]') || document.body.firstElementChild;
                if (!testEl) return this.keys;
                for (const prop of Object.keys(testEl)) {
                    if (prop.startsWith('__reactFiber$')) { this.keys.fiber = prop; this.keys.suffix = prop.replace('__reactFiber$', ''); }
                    else if (prop.startsWith('__reactProps$')) { this.keys.props = prop; }
                    else if (prop.startsWith('__reactContainer$')) { this.keys.container = prop; }
                }
                console.log('[ReactExplorer] Keys:', this.keys);
                return this.keys;
            },
            getFiber: function (el) {
                const keys = this.findReactKeys();
                return keys.fiber ? el[keys.fiber] : null;
            },
            findParentFiber: function (fiber, predicate, max = 30) {
                let c = fiber, d = 0;
                while (c && d < max) { if (predicate(c)) return c; c = c.return; d++; }
                return null;
            },
            explore: function () {
                console.log('[ReactExplorer] === STARTING ===');
                const keys = this.findReactKeys();
                if (!keys.fiber) { console.warn('[ReactExplorer] No React keys found!'); return; }

                // Find Make video button
                const btn = document.querySelector('button[aria-label="Make video"]');
                if (btn) {
                    const f = this.getFiber(btn);
                    console.log('[ReactExplorer] Make video btn:', btn);
                    console.log('[ReactExplorer] Button fiber:', f);
                    if (f && f.memoizedProps) console.log('[ReactExplorer] Props:', f.memoizedProps);
                } else { console.log('[ReactExplorer] Make video button not on page'); }

                // Find video elements
                const vids = document.querySelectorAll('video, [class*="video"]');
                console.log('[ReactExplorer] Video elements:', vids.length);
                vids.forEach((v, i) => {
                    const f = this.getFiber(v);
                    if (f) {
                        const parent = this.findParentFiber(f, p => p.memoizedState && typeof p.memoizedState === 'object');
                        if (parent) console.log(`[ReactExplorer] Video ${i} stateful parent:`, parent.type?.name, parent.memoizedState);
                    }
                });

                // Find React root
                const root = document.getElementById('__next') || document.getElementById('root');
                if (root) {
                    const rf = root[keys.container] || this.getFiber(root);
                    console.log('[ReactExplorer] Root fiber:', rf ? 'FOUND' : 'NOT FOUND');
                }

                window.__grokReact = { keys, explorer: this };
                console.log('[ReactExplorer] === DONE === Access: window.__grokReact');
            },
            clickMakeVideo: function () {
                const btn = document.querySelector('button[aria-label="Make video"]');
                if (!btn) { console.warn('[ReactExplorer] No Make video button'); return false; }
                ['mousedown', 'mouseup', 'click'].forEach(t => btn.dispatchEvent(new MouseEvent(t, { bubbles: true, cancelable: true, view: window })));
                console.log('[ReactExplorer] Click sequence fired');
                return true;
            }
        };
        window.GrokReactExplorer = ReactExplorer;

        // --- UI BUILDER ---
        function createPanel() {
            if (document.getElementById('grok-dev-panel')) return;

            const style = document.createElement('style');
            style.innerHTML = [
                "#grok-dev-panel { position: fixed; width: 500px; height: 600px; min-width: 350px; min-height: 300px; background: #09090b; color: #e4e4e7; border: 1px solid #27272a; border-radius: 12px; z-index: 9999999; font-family: monospace; box-shadow: 0 10px 40px rgba(0,0,0,0.5); display: flex; flex-direction: column; resize: both; overflow: hidden; font-size: 12px; }",
                "#grok-dev-header { padding: 12px 16px; background: #18181b; border-bottom: 1px solid #27272a; font-weight: bold; cursor: move; display: flex; justify-content: space-between; align-items: center; user-select: none; flex-shrink: 0; }",
                "#grok-dev-controls { padding: 10px; border-bottom: 1px solid #27272a; display: flex; gap: 8px; background: #09090b; flex-shrink: 0; }",
                ".grok-btn { background: #27272a; border: none; color: #fff; padding: 8px 12px; border-radius: 6px; cursor: pointer; flex: 1; font-weight: 600; transition: all 0.2s; white-space: nowrap; }",
                ".grok-btn:hover { background: #3f3f46; }",
                ".grok-btn-primary { background: #2563eb; color: white; }",
                ".grok-btn-primary:hover { background: #1d4ed8; }",
                "#grok-req-list { overflow-y: auto; flex: 1; padding: 10px; display: flex; flex-direction: column; gap: 8px; }",
                "#grok-req-list::-webkit-scrollbar { width: 8px; }",
                "#grok-req-list::-webkit-scrollbar-track { background: #18181b; }",
                "#grok-req-list::-webkit-scrollbar-thumb { background: #3f3f46; border-radius: 4px; }",
                ".req-group { background: #18181b; border: 1px solid #27272a; border-radius: 8px; overflow: hidden; transition: background 0.2s; flex-shrink: 0; }",
                ".req-summary { padding: 8px; cursor: pointer; position: relative; user-select: none; display: flex; gap: 10px; align-items: flex-start; }",
                ".req-summary:hover { background: #27272a; }",
                ".req-thumb { width: 48px; height: 48px; border-radius: 6px; object-fit: cover; background: #222; flex-shrink: 0; border: 1px solid #333; margin-top: 2px; }",
                ".req-info-col { flex: 1; min-width: 0; display: flex; flex-direction: column; gap: 4px; }",
                ".req-header-row { display: flex; justify-content: space-between; color: #71717a; font-size: 11px; align-items: center; }",
                ".req-title { font-weight: bold; color: #facc15; font-size: 12px; display: -webkit-box; -webkit-line-clamp: 2; -webkit-box-orient: vertical; overflow: hidden; line-height: 1.4; }",
                ".req-status { font-weight: bold; color: #a1a1aa; font-size: 11px; display: flex; justify-content: space-between; align-items: center; }",
                ".req-badge { background: #3f3f46; color: #fff; padding: 1px 5px; border-radius: 4px; font-size: 10px; margin-left: 6px; }",
                ".req-id-tag { font-family: monospace; color: #555; font-size: 9px; margin-left: 6px; border: 1px solid #333; padding: 0 4px; border-radius: 3px; }",
                ".req-progress-bg { position: absolute; bottom: 0; left: 0; height: 3px; background: #3b82f6; width: 0%; transition: width 0.3s ease; }",
                ".req-progress-bg.moderated { background: #ef4444 !important; }",
                ".req-progress-bg.done { background: #22c55e !important; }",
                ".req-details { display: none; padding: 0; border-top: 1px solid #27272a; background: #000000; }",
                ".req-group.expanded .req-details { display: block; }",
                ".req-group.expanded .req-summary { background: #27272a; }",
                ".history-list { display: flex; flex-direction: column; }",
                ".history-item { display: flex; align-items: center; padding: 8px 12px; border-bottom: 1px solid #222; font-size: 11px; color: #888; justify-content: space-between; }",
                ".history-item:last-child { border-bottom: none; }",
                ".history-meta { display: flex; gap: 8px; align-items: center; }",
                ".history-status { font-weight: bold; min-width: 60px; }",
                "#grok-dev-minimized { position: fixed; width: 48px; height: 48px; background: #2563eb; color: white; border-radius: 50%; z-index: 9999999; box-shadow: 0 4px 15px rgba(0,0,0,0.5); display: none; align-items: center; justify-content: center; cursor: pointer; border: 2px solid #60a5fa; transition: transform 0.1s; font-size: 20px; user-select: none; }",
                "#grok-dev-minimized:hover { transform: scale(1.1); }",
                ".min-btn { cursor: pointer; padding: 4px 8px; border-radius: 4px; background: #27272a; color: #a1a1aa; font-family: sans-serif; }",
                ".btn-icon { width: 24px; height: 24px; border-radius: 4px; background: #27272a; color: #fff; border: 1px solid #3f3f46; display: flex; align-items: center; justify-content: center; cursor: pointer; font-size: 12px; margin-left: 8px; }",
                ".btn-icon:hover { background: #3b82f6; border-color: #2563eb; }",
                ".btn-view { width: auto; padding: 0 8px; font-size: 10px; margin-left: 4px; }",
                "#btn-load-more { width: 100%; padding: 10px; background: #27272a; border: 1px dashed #3f3f46; color: #a1a1aa; cursor: pointer; margin-top: 10px; border-radius: 6px; font-size: 11px; }",
                "#btn-load-more:hover { background: #3f3f46; color: white; }"
            ].join("\n");

            document.head.appendChild(style);

            const panel = document.createElement('div');
            panel.id = 'grok-dev-panel';
            panel.style.top = '150px';
            panel.style.left = '20px';

            panel.innerHTML = `
                <div id="grok-dev-header">
                    <span>\uD83D\uDCFA Monitor v26.0 (GodMode)</span>
                    <div style="display:flex; gap:8px;">
                        <div id="btn-minimize" class="min-btn" title="Minimize">_</div>
                    </div>
                </div>
                <div id="grok-dev-controls">
                    <button id="btn-soft-refresh" class="grok-btn grok-btn-primary">\u26A1 Refresh</button>
                    <button id="btn-explore" class="grok-btn" style="background:#7c3aed;">\uD83D\uDD2C React</button>
                    <button id="btn-clear" class="grok-btn">Clear</button>
                </div>
                <div id="grok-req-list">
                    <div id="empty-msg" style="padding:20px; text-align:center; color:#555;">Waiting for Page Data...</div>
                </div>
            `;
            document.body.appendChild(panel);

            const miniBtn = document.createElement('div');
            miniBtn.id = 'grok-dev-minimized';
            miniBtn.innerHTML = '\uD83D\uDCFA';
            document.body.appendChild(miniBtn);

            setupDragging(panel, document.getElementById('grok-dev-header'));
            setupDragging(miniBtn, miniBtn, true);

            document.getElementById('btn-minimize').addEventListener('click', () => toggleMinimize(true));
            miniBtn.addEventListener('click', () => toggleMinimize(false));

            document.getElementById('btn-soft-refresh').addEventListener('click', triggerManualRefresh);
            document.getElementById('btn-explore').addEventListener('click', () => {
                console.log('[GrokDev] Running React exploration...');
                ReactExplorer.explore();
            });
            document.getElementById('btn-clear').addEventListener('click', () => {
                requests.clear();
                groups.clear();
                historyData = [];
                historyOffset = 0;
                document.getElementById('grok-req-list').innerHTML = '<div id="empty-msg" style="padding:20px; text-align:center; color:#555;">Waiting for Page Data...</div>';
            });

            function toggleMinimize(minimize) {
                if (minimize) {
                    miniBtn.style.top = panel.style.top; miniBtn.style.left = panel.style.left;
                    panel.style.display = 'none'; miniBtn.style.display = 'flex';
                } else {
                    panel.style.top = miniBtn.style.top; panel.style.left = miniBtn.style.left;
                    miniBtn.style.display = 'none'; panel.style.display = 'flex';
                }
            }
        }

        function setupDragging(el, handle, isButton = false) {
            let isDragging = false, startX, startY, iLeft, iTop;
            handle.addEventListener('mousedown', (e) => {
                if (e.target.tagName === 'BUTTON' || e.target.classList.contains('min-btn')) return;
                isDragging = true; startX = e.clientX; startY = e.clientY;
                const rect = el.getBoundingClientRect(); iLeft = rect.left; iTop = rect.top;
                el.style.right = 'auto'; el.style.bottom = 'auto';
                el.style.width = el.style.width; el.style.height = el.style.height;
            });
            document.addEventListener('mousemove', (e) => {
                if (!isDragging) return;
                e.preventDefault();
                el.style.left = (iLeft + (e.clientX - startX)) + 'px';
                el.style.top = (iTop + (e.clientY - startY)) + 'px';
            });
            document.addEventListener('mouseup', () => {
                if (isDragging) {
                    isDragging = false;
                    panelState.left = el.style.left;
                    panelState.top = el.style.top;
                }
            });
        }

        // --- GOD MODE NAVIGATION (THE HACK) ---
        function godModeSnap(targetId) {
            if (!targetId) return;
            const btn = document.querySelector(`[id^='view-'][id*='${targetId.slice(0, 5)}']`);
            if (btn) btn.innerText = "\u26A1 Snapping...";

            const targetUrl = `/imagine/post/${targetId}`;
            console.log(`GrokDev: God Mode Snap to ${targetUrl}`);

            // 1. RAW HISTORY PUSH (Changes URL Instantly)
            window.history.pushState({}, "", targetUrl);

            // 2. FAKE EVENT (Wakes up Next.js / React)
            window.dispatchEvent(new PopStateEvent("popstate"));

            if (btn) btn.innerText = "\u2197 View";
        }

        // --- HISTORY RENDERER ---
        function processHistoryData(data) {
            if (!data || !data.posts) return;
            console.log("GrokDev: Intercepted history data:", data.posts.length, "items");
            const newPosts = data.posts.filter(p => !historyData.find(h => h.postId === p.postId));
            historyData = [...historyData, ...newPosts];
            renderHistoryBatch();
        }

        function renderHistoryBatch() {
            const container = document.getElementById('grok-req-list');
            const emptyMsg = document.getElementById('empty-msg');
            if (emptyMsg) emptyMsg.remove();

            const oldBtn = document.getElementById('btn-load-more');
            if (oldBtn) oldBtn.remove();

            const slice = historyData.slice(historyOffset, historyOffset + 20);

            slice.forEach(post => {
                const id = post.postId || post.id;
                if (document.getElementById('hist-' + id)) return;

                const prompt = post.prompt || "History Item";
                const imgUrl = post.thumbnailUrl || post.mediaUrl || "";

                let dateStr = "Saved";
                if (post.createTime) dateStr = new Date(post.createTime * 1000).toLocaleTimeString();
                else if (post.createdAt) dateStr = new Date(post.createdAt * 1000).toLocaleTimeString();

                const el = document.createElement('div');
                el.className = 'req-group';
                el.id = 'hist-' + id;
                const shortId = id.slice(0, 4) + '..';

                el.innerHTML = `
                    <div class="req-summary" style="opacity: 0.9; border-left: 3px solid #555;">
                        <img src="${imgUrl}" class="req-thumb" alt="">
                        <div class="req-info-col">
                            <div class="req-header-row">
                                <span>${dateStr} <span class="req-id-tag">ID:${shortId}</span></span>
                                <button class="btn-icon btn-view" id="view-${id}" title="God Mode Snap">\u2197 View</button>
                            </div>
                            <span class="req-title">${prompt}</span>
                            <div class="req-status"><span class="status-text">History</span></div>
                        </div>
                    </div>
                `;
                container.appendChild(el);

                el.querySelector(`#view-${id}`).addEventListener('click', (e) => {
                    e.stopPropagation();
                    godModeSnap(id);
                });
            });

            historyOffset += slice.length;

            if (historyOffset < historyData.length) {
                const moreBtn = document.createElement('button');
                moreBtn.id = 'btn-load-more';
                moreBtn.innerText = `[+] Load More (${historyData.length - historyOffset} remaining)`;
                moreBtn.addEventListener('click', renderHistoryBatch);
                container.appendChild(moreBtn);
            }
        }

        // --- FETCH INTERCEPTOR (Unified) ---
        const originalFetch = window.fetch;
        window.fetch = async function (...args) {
            const [resource, config] = args;
            const url = resource ? resource.toString() : "";

            // 1. Intercept NEW Generations (Live Monitor)
            if (url.includes(TARGET_ENDPOINT) && config && config.method === 'POST') {
                const reqId = Date.now();
                let prompt = "Video Request";
                let imgUrl = null;
                let groupId = null;
                try {
                    const body = JSON.parse(config.body);
                    if (body.message) {
                        const imgMatch = body.message.match(/(https?:\/\/[^\s]+)/);
                        if (imgMatch) imgUrl = imgMatch[0];
                        prompt = body.message.replace(/(https?:\/\/[^\s]+)/g, '').replace(/--mode=\w+/, '').trim();
                    }
                    if (body.responseMetadata?.modelConfigOverride?.modelMap?.videoGenModelConfig?.parentPostId) {
                        groupId = body.responseMetadata.modelConfigOverride.modelMap.videoGenModelConfig.parentPostId;
                    } else if (body.metadata?.modelConfigOverride?.modelMap?.videoGenModelConfig?.parentPostId) {
                        groupId = body.metadata.modelConfigOverride.modelMap.videoGenModelConfig.parentPostId;
                    } else if (body.fileAttachments && body.fileAttachments.length > 0) {
                        groupId = body.fileAttachments[0];
                    } else if (imgUrl) {
                        const idMatch = imgUrl.match(/([a-f0-9\-]{36})/);
                        if (idMatch) groupId = idMatch[1];
                    }
                    if (!groupId) groupId = "txt_" + reqId;
                } catch (e) { groupId = 'err_' + reqId; }

                const reqObj = { id: reqId, groupId: groupId, time: new Date().toLocaleTimeString(), prompt: prompt, imgUrl: imgUrl, config: config, url: url, progress: 0, status: 'Starting...', isModerated: false, isDone: false };
                requests.set(reqId, reqObj);
                if (!groups.has(groupId)) groups.set(groupId, new Set());
                groups.get(groupId).add(reqId);
                setTimeout(() => addRequestToUI(reqObj), 0);

                try {
                    const response = await originalFetch.apply(this, args);
                    const clone = response.clone();
                    readStream(clone, reqId);
                    return response;
                } catch (e) {
                    reqObj.status = "Failed";
                    setTimeout(() => updateItemUI(reqId), 0);
                    throw e;
                }
            }

            // 2. Intercept HISTORY List (Passive Leech)
            if (url.includes(LIST_ENDPOINT) && (!config || config.method === 'POST')) {
                try {
                    const response = await originalFetch.apply(this, args);
                    const clone = response.clone();
                    clone.json().then(data => processHistoryData(data)).catch(e => { });
                    return response;
                } catch (e) { return originalFetch.apply(this, args); }
            }

            return originalFetch.apply(this, args);
        };

        async function readStream(response, reqId) {
            const reader = response.body.getReader();
            const decoder = new TextDecoder("utf-8");
            let buffer = "";
            while (true) {
                const { done, value } = await reader.read();
                if (done) break;
                const chunk = decoder.decode(value, { stream: true });
                buffer += chunk;
                if (/"moderated":\s*true/.test(buffer)) {
                    const req = requests.get(reqId);
                    if (req) { req.isModerated = true; req.progress = 100; req.status = "Ã¢â€ºâ€ Flagged"; req.isDone = true; updateItemUI(reqId); }
                }
                const req = requests.get(reqId);
                if (req && !req.isModerated) {
                    const progressMatch = buffer.match(/"progress":\s*(\d+)/g);
                    if (progressMatch) {
                        const percent = parseInt(progressMatch[progressMatch.length - 1].match(/\d+/)[0]);
                        req.progress = percent; req.status = "Generating " + percent + "%"; updateItemUI(reqId);
                    }
                }
            }
            const req = requests.get(reqId);
            if (req && !req.isModerated) { req.progress = 100; req.status = "Ã¢Å“â€¦ Success"; req.isDone = true; updateItemUI(reqId); }
        }

        function addRequestToUI(req) {
            const container = document.getElementById('grok-req-list');
            if (!container) return;
            const emptyMsg = document.getElementById('empty-msg');
            if (emptyMsg) emptyMsg.remove();

            let groupEl = document.getElementById('group-' + req.groupId);
            const thumbSrc = req.imgUrl ? req.imgUrl : 'data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIHdpZHRoPSI0MCIgaGVpZ2h0PSI0MCIgdmlld0JveD0iMCAwIDQwIDQwIj48cmVjdCB3aWR0aD0iNDAiIGhlaWdodD0iNDAiIGZpbGw9IiMzMzMiLz48L3N2Zz4=';
            const shortId = req.groupId.startsWith('txt_') ? 'TXT' : req.groupId.slice(0, 4) + '..';

            if (!groupEl) {
                groupEl = document.createElement('div');
                groupEl.className = 'req-group';
                groupEl.id = 'group-' + req.groupId;
                groupEl.dataset.latestId = req.id;
                groupEl.innerHTML = `
                    <div class="req-summary">
                        <div class="req-progress-bg" id="prog-${req.groupId}" style="width: 0%"></div>
                        <img src="${thumbSrc}" class="req-thumb" alt="">
                        <div class="req-info-col">
                            <div class="req-header-row">
                                <span>${req.time} <span class="req-badge" id="badge-${req.groupId}">(1)</span> <span class="req-id-tag">ID:${shortId}</span></span>
                                <div style="display:flex;">
                                    <button class="btn-icon" id="replay-${req.groupId}" title="Replay Latest">\u21BA</button>
                                    <button class="btn-icon btn-view" id="view-${req.groupId}" title="God Mode Snap">\u2197 View</button>
                                </div>
                            </div>
                            <span class="req-title">${req.prompt}</span>
                            <div class="req-status"><span class="status-text" id="status-${req.groupId}">Starting...</span></div>
                        </div>
                    </div>
                    <div class="req-details"><div class="history-list" id="hist-${req.groupId}"></div></div>`;
                container.insertBefore(groupEl, container.firstChild);
                groupEl.querySelector('.req-summary').addEventListener('click', () => groupEl.classList.toggle('expanded'));
                groupEl.querySelector(`#replay-${req.groupId}`).addEventListener('click', (e) => { e.stopPropagation(); replayRequest(req.id); });
                groupEl.querySelector(`#view-${req.groupId}`).addEventListener('click', (e) => { e.stopPropagation(); godModeSnap(req.groupId); });
            } else {
                groupEl.dataset.latestId = req.id;
                const count = groups.get(req.groupId).size;
                document.getElementById(`badge-${req.groupId}`).innerText = `(${count})`;
                document.getElementById(`title-${req.groupId}`).innerText = req.prompt;
                container.insertBefore(groupEl, container.firstChild);
            }
        }

        function updateItemUI(id) {
            const req = requests.get(id);
            if (!req) return;
            const groupEl = document.getElementById('group-' + req.groupId);
            if (groupEl && groupEl.dataset.latestId == id) {
                const statusEl = document.getElementById(`status-${req.groupId}`);
                const bar = document.getElementById(`prog-${req.groupId}`);
                if (statusEl) statusEl.innerText = req.status;
                if (bar) {
                    bar.style.width = req.progress + '%';
                    bar.classList.remove('moderated', 'done');
                    if (req.isModerated) bar.classList.add('moderated');
                    else if (req.status.includes("Success")) bar.classList.add('done');
                }
            }
        }

        async function replayRequest(id) {
            const req = requests.get(Number(id));
            if (!req) return;
            const newConfig = { ...req.config }; delete newConfig.signal; newConfig.credentials = 'include';
            await window.fetch(req.url, newConfig);
        }

        function triggerManualRefresh() {
            window.history.replaceState({}, "", window.location.href);
            window.dispatchEvent(new PopStateEvent("popstate"));
        }

        const observer = new MutationObserver(() => {
            if (document.body) { createPanel(); observer.disconnect(); }
        });
        observer.observe(document.documentElement, { childList: true });
    }

    const script = document.createElement('script');
    script.textContent = "(" + main.toString() + ")();";
    (document.head || document.documentElement).appendChild(script);
    script.remove();

})();

