/**
 * AutoplayControlManager.js
 * Manages video autoplay behavior on Grok feed pages
 * Integrates with GVP extension architecture
 */

class AutoplayControlManager {
    constructor() {
        this.config = {
            enabled: true,
            glowColors: {
                hadAutoplay: '#f9f8f6',
                noAutoplay: '#fcfcfc1a'
            },
            glowBlur: '12px',
            glowSpread: '0px',
            performanceMonitoring: false
        };

        // Performance tracking
        this.stats = {
            processCount: 0,
            totalTime: 0,
            maxTime: 0
        };

        // Cache processed videos
        this.processedVideos = new WeakSet();

        // MutationObserver
        this.observer = null;

        // Throttle control
        this.isProcessing = false;
        this.processingQueue = [];

        Logger.log('[AutoplayControlManager] Initialized');
    }

    /**
     * Initialize the manager
     * Called from content.js after all managers are loaded
     */
    async init() {
        try {
            // Load settings from storage
            await this.loadSettings();

            // Check if we're on a feed page (not individual post)
            if (!this.shouldRunOnCurrentPage()) {
                Logger.log('[AutoplayControlManager] Skipping - on individual post page');
                return;
            }

            // Process existing videos
            this.processExistingVideos();

            // Start observing for new videos
            this.startObserver();

            // Setup performance monitoring if enabled
            if (this.config.performanceMonitoring) {
                this.startPerformanceMonitoring();
            }

            Logger.log('[AutoplayControlManager] Started on feed page');
        } catch (error) {
            Logger.error('[AutoplayControlManager] Initialization error:', error);
        }
    }

    /**
     * Load settings from StorageManager
     */
    async loadSettings() {
        try {
            const stored = await StorageManager.get('autoplayControl');
            if (stored) {
                this.config = { ...this.config, ...stored };
            }
        } catch (error) {
            Logger.error('[AutoplayControlManager] Settings load error:', error);
        }
    }

    /**
     * Save settings to StorageManager
     */
    async saveSettings() {
        try {
            await StorageManager.set('autoplayControl', this.config);
            Logger.log('[AutoplayControlManager] Settings saved');
        } catch (error) {
            Logger.error('[AutoplayControlManager] Settings save error:', error);
        }
    }

    /**
     * Check if manager should run on current page
     * Skip individual post pages, run on feed/favorites/explore
     */
    shouldRunOnCurrentPage() {
        const url = window.location.pathname;

        // Don't run on individual post pages
        if (url.includes('/post/')) {
            return false;
        }

        // Run on feed pages
        return url.includes('/imagine');
    }

    /**
     * Process all existing videos on page load
     */
    processExistingVideos() {
        const videos = document.querySelectorAll('video');
        Logger.log(`[AutoplayControlManager] Processing ${videos.length} existing videos`);

        videos.forEach(video => {
            this.processVideo(video);
        });
    }

    /**
     * Start MutationObserver for dynamically loaded videos
     */
    startObserver() {
        this.observer = new MutationObserver((mutations) => {
            if (this.isProcessing) return;

            mutations.forEach(mutation => {
                mutation.addedNodes.forEach(node => {
                    if (node.nodeType === Node.ELEMENT_NODE) {
                        // Check if node is a video
                        if (node.tagName === 'VIDEO') {
                            this.queueVideo(node);
                        }
                        // Check if node contains videos
                        else if (node.querySelectorAll) {
                            const videos = node.querySelectorAll('video');
                            videos.forEach(video => this.queueVideo(video));
                        }
                    }
                });
            });

            // Process queue with throttling
            this.processQueue();
        });

        this.observer.observe(document.body, {
            childList: true,
            subtree: true
        });

        Logger.log('[AutoplayControlManager] MutationObserver started');
    }

    /**
     * Add video to processing queue
     */
    queueVideo(video) {
        if (!this.processedVideos.has(video)) {
            this.processingQueue.push(video);
        }
    }

    /**
     * Process queued videos with throttling
     */
    processQueue() {
        if (this.isProcessing || this.processingQueue.length === 0) {
            return;
        }

        this.isProcessing = true;

        // Use requestIdleCallback for non-blocking processing
        requestIdleCallback((deadline) => {
            const startTime = performance.now();

            while (this.processingQueue.length > 0) {
                // Stop if we've used more than 16ms (1 frame)
                if (performance.now() - startTime > 16) {
                    // Continue in next idle period
                    requestIdleCallback(() => this.processQueue());
                    break;
                }

                // Stop if browser is busy
                if (deadline.timeRemaining() < 1) {
                    break;
                }

                const video = this.processingQueue.shift();
                this.processVideo(video);
            }

            this.isProcessing = false;

            // Continue if more videos in queue
            if (this.processingQueue.length > 0) {
                this.processQueue();
            }
        });
    }

    /**
     * Process a single video element
     * Main logic for disabling autoplay and adding visual indicators
     */
    processVideo(video) {
        // Skip if already processed
        if (this.processedVideos.has(video)) {
            return;
        }

        const startTime = performance.now();

        try {
            // Store original autoplay/loop state
            const hadAutoplay = video.hasAttribute('autoplay');
            const hadLoop = video.hasAttribute('loop');

            // Store in dataset for reference
            video.dataset.originalAutoplay = hadAutoplay.toString();
            video.dataset.originalLoop = hadLoop.toString();

            // Disable autoplay and loop
            if (hadAutoplay) {
                video.removeAttribute('autoplay');
            }
            if (hadLoop) {
                video.removeAttribute('loop');
            }

            // Pause if playing
            if (!video.paused) {
                video.pause();
            }

            // Reset to beginning
            if (video.currentTime > 0) {
                video.currentTime = 0;
            }

            // Set preload to metadata to save bandwidth
            video.setAttribute('preload', 'metadata');

            // Find parent card container
            const cardContainer = this.findCardContainer(video);

            if (cardContainer) {
                // Apply glow effect based on whether it had autoplay
                const glowColor = hadAutoplay 
                    ? this.config.glowColors.hadAutoplay 
                    : this.config.glowColors.noAutoplay;

                this.applyGlowEffect(cardContainer, glowColor);

                // Setup hover-to-play for videos that had autoplay
                if (hadAutoplay) {
                    this.setupHoverToPlay(video, cardContainer, hadLoop);
                }
            }

            // Mark as processed
            this.processedVideos.add(video);
            video.dataset.autoplayDisabled = 'true';

            // Performance tracking
            const duration = performance.now() - startTime;
            this.stats.processCount++;
            this.stats.totalTime += duration;
            this.stats.maxTime = Math.max(this.stats.maxTime, duration);

            if (this.config.performanceMonitoring && duration > 16) {
                Logger.warn(`[AutoplayControlManager] Slow processing: ${duration.toFixed(2)}ms`);
            }

        } catch (error) {
            Logger.error('[AutoplayControlManager] Error processing video:', error);
        }
    }

    /**
     * Find the parent card container element
     */
    findCardContainer(video) {
        let element = video;
        let depth = 0;
        const maxDepth = 10; // Limit search depth

        while (element && depth < maxDepth) {
            element = element.parentElement;
            if (!element) break;

            // Check for listitem or masonry card
            if (element.getAttribute('role') === 'listitem' ||
                element.classList.contains('group/media-post-masonry-card')) {
                return element;
            }

            depth++;
        }

        return null;
    }

    /**
     * Apply glow effect to card container
     */
    applyGlowEffect(container, color) {
        if (!container) return;

        container.style.boxShadow = `0 0 ${this.config.glowBlur} ${this.config.glowSpread} ${color}`;
        container.style.borderRadius = '1rem';
    }

    /**
     * Setup hover-to-play functionality
     */
    setupHoverToPlay(video, container, hadLoop) {
        if (!container) return;

        // Prevent duplicate listeners
        if (container.dataset.hoverEnabled === 'true') {
            return;
        }

        const handleMouseEnter = () => {
            if (hadLoop) {
                video.loop = true;
            }
            video.play().catch(err => {
                Logger.debug('[AutoplayControlManager] Play error:', err);
            });
        };

        const handleMouseLeave = () => {
            video.pause();
            video.loop = false;
            video.currentTime = 0;
        };

        container.addEventListener('mouseenter', handleMouseEnter);
        container.addEventListener('mouseleave', handleMouseLeave);

        container.dataset.hoverEnabled = 'true';
    }

    /**
     * Start performance monitoring
     */
    startPerformanceMonitoring() {
        setInterval(() => {
            if (this.stats.processCount > 0) {
                const avgTime = this.stats.totalTime / this.stats.processCount;
                Logger.log('[AutoplayControlManager] Performance Stats:', {
                    processed: this.stats.processCount,
                    avgTime: avgTime.toFixed(2) + 'ms',
                    maxTime: this.stats.maxTime.toFixed(2) + 'ms',
                    totalTime: this.stats.totalTime.toFixed(2) + 'ms'
                });

                // Reset stats
                this.stats.processCount = 0;
                this.stats.totalTime = 0;
                this.stats.maxTime = 0;
            }
        }, 10000); // Every 10 seconds
    }

    /**
     * Toggle autoplay control on/off
     */
    async toggle(enabled) {
        this.config.enabled = enabled;
        await this.saveSettings();

        if (enabled && !this.observer) {
            this.startObserver();
        } else if (!enabled && this.observer) {
            this.observer.disconnect();
            this.observer = null;
        }

        Logger.log(`[AutoplayControlManager] ${enabled ? 'Enabled' : 'Disabled'}`);
    }

    /**
     * Update glow colors
     */
    async updateGlowColors(hadAutoplay, noAutoplay) {
        this.config.glowColors.hadAutoplay = hadAutoplay;
        this.config.glowColors.noAutoplay = noAutoplay;
        await this.saveSettings();

        // Reprocess existing videos with new colors
        this.processedVideos = new WeakSet();
        this.processExistingVideos();

        Logger.log('[AutoplayControlManager] Glow colors updated');
    }

    /**
     * Cleanup on extension unload
     */
    destroy() {
        if (this.observer) {
            this.observer.disconnect();
            this.observer = null;
        }

        this.processingQueue = [];
        this.processedVideos = new WeakSet();

        Logger.log('[AutoplayControlManager] Destroyed');
    }
}

// Create singleton instance
window.AutoplayControlManager = new AutoplayControlManager();

